import React, { Component } from 'react';

export default class Dropdown extends Component {
    constructor() {
        super();

        this.state = {
            showMenu: false,
        }

        this.showMenu = this.showMenu.bind(this);
    }

    showMenu(event) {
        event.preventDefault();

        this.setState({
            showMenu: true,
        });
    }


    render () {
        return (
            <div>
                <button>
                    Select folder
                </button>
            {
                this.state.showMenu
                ?(
                <div>
                    <button> Folder Name </button>
                </div>
                ) 
                : (
                    null
                    )
                }
            </div>
        );
    }
}
