            <div>
                <NotePageNav />
                <form onSubmit={this.handleSubmit}>
                    <label>Folder Name:
                    <input type="text" name="newFolder" value={this.state.value} onChange={this.handleChange} />
                    </label>

                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}
